                    class Token {

                        constructor(tokenInstance) {
                            this.props = tokenInstance;
                        }

                        render() {
                            return`
            <div>Symbol: ${this.props.symbol}</div>
            <div>Name: ${this.props.name}</div>
            <div>Balance: ${this.props.ownerBalance}</div>
            <div>Decimals ${this.props.decimals}</div>

`;
                        }
                    }

                    web3.tokens.dataChanged = (oldTokens, updatedTokens, tokenIdCard) => {
                        document.getElementById('mainid').innerHTML = new Token(web3.tokens.data.currentInstance).render();
                    };
